#import "LevelData.h"

@implementation LevelData

+(LevelData *)sharedLevelData
{
    static LevelData *instance = nil;
    static dispatch_once_t onceToken;
    dispatch_once(&onceToken, ^{
        instance = [[self alloc] init];
    });
    return instance;
}

+(int)numberOfLevels
{
    int count = (int)[[[LevelData sharedLevelData] getLevels] count];
    return count;
}

+(NSArray *)getLevelForId:(int)levelId
{
    NSArray *levels = [[LevelData sharedLevelData] getLevels];
    return levels[levelId-1];
}

